pragma solidity >=0.6.0;

interface AggregatorInterface {
  function latestAnswer() external view returns (int256);
  function latestTimestamp() external view returns (uint256);
  function latestRound() external view returns (uint256);
  function getAnswer(uint256 roundId) external view returns (int256);
  function getTimestamp(uint256 roundId) external view returns (uint256);

  event AnswerUpdated(int256 indexed current, uint256 indexed roundId, uint256 updatedAt);
  event NewRound(uint256 indexed roundId, address indexed startedBy, uint256 startedAt);
}

pragma solidity >=0.6.0;

interface AggregatorV3Interface {

  function decimals() external view returns (uint8);
  function description() external view returns (string memory);
  function version() external view returns (uint256);

  // getRoundData and latestRoundData should both raise "No data present"
  // if they do not have data to report, instead of returning unset values
  // which could be misinterpreted as actual reported values.
  function getRoundData(uint80 _roundId)
    external
    view
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    );
  function latestRoundData()
    external
    view
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    );

}

pragma solidity >=0.6.0;

interface AggregatorV2V3Interface is AggregatorInterface, AggregatorV3Interface
{
}

pragma solidity ^0.8.0;
contract tradeBook {

   using SafeERC20 for IERC20;

   struct trade { 
      uint256 orderTime;
      address addressOfInitiator;
      address addressOfInitiatorToken;
      uint256 amountOfInitiatorToken;
      address addressOfTaker;
      address addressOfTakerToken;
      uint256 amountOfTakerToken;
      uint256 tradeCloseTime;
      bool tradeStatus;
   } 

   mapping(uint256 => trade) public tradeSheet;


   uint256 public tradeIndex=0;

   function initializeTrade(address _iasset, uint256 _iamount,address _tasset) public {
      
      tradeIndex+=1;
      trade memory newTrade = trade(
         block.timestamp,
         msg.sender,
         _iasset,
         _iamount,
         address(0),
         _tasset,
         0,
         0,
         false
         );
      
      IERC20 tokenERC = IERC20(_iasset);

      tokenERC.safeTransferFrom(msg.sender, address(this), _iamount);

      tradeSheet[tradeIndex] = newTrade;

   }

   function takeTrade(uint256 tradeID) public{
      
      trade memory currentTrade = tradeSheet[tradeID];

      require(currentTrade.tradeStatus == false,"Trade has already been completed");
      require(msg.sender != currentTrade.addressOfInitiator,"You cannot accept your own trade");

      address sourceAsset = currentTrade.addressOfInitiatorToken;
      address targeAsset = currentTrade.addressOfTakerToken;

      uint256 sourceUsd = currtousd(currentTrade.amountOfInitiatorToken,sourceAsset);
      uint256 targetCur = usdtocurr(sourceUsd,targeAsset);

      currentTrade.addressOfTaker = msg.sender;
      currentTrade.amountOfTakerToken = targetCur;
      currentTrade.tradeCloseTime = block.timestamp;
      currentTrade.tradeStatus = true;

      IERC20 tokenSource = IERC20(sourceAsset);
      IERC20 tokenTarget = IERC20(targeAsset);

      tokenSource.safeTransfer(msg.sender, currentTrade.amountOfInitiatorToken);
      tokenTarget.safeTransferFrom(msg.sender, currentTrade.addressOfInitiator, currentTrade.amountOfTakerToken);

   }

   function costreturn(uint256 tradeID) view public returns(uint256){
      trade memory currentTrade = tradeSheet[tradeID];
      address sourceAsset = currentTrade.addressOfInitiatorToken;
      address targeAsset = currentTrade.addressOfTakerToken;
      uint256 sourceUsd = currtousd(currentTrade.amountOfInitiatorToken,sourceAsset);
      uint256 targetCur = usdtocurr(sourceUsd,targeAsset);
      return(targetCur);
   }

   function currtousd(uint256 curr,address targetAsset) view public returns(uint256){
      address oracleAdr = address(0);
      if (targetAsset == 0x90cE3cF973D781453a38a15ccb136fbF18381A38) {
         oracleAdr = 0xECe365B379E1dD183B20fc5f022230C044d51404;
      } 
      if (targetAsset == 0xc8C3A0d984c7073d1557c5E6F861BA7f81678738) {
         oracleAdr = 0x8A753747A1Fa494EC906cE90E9f37563A8AF630e;
      }
      AggregatorV2V3Interface priceFeedCurrency = AggregatorV2V3Interface(oracleAdr);
      uint256 currval = (curr*(uint(priceFeedCurrency.latestAnswer())))/((10**18));
      return currval;
  }
  
  function usdtocurr(uint256 usd,address targetAsset) view public returns(uint256){
      
      address oracleAdr = address(0);
      if (targetAsset == 0x90cE3cF973D781453a38a15ccb136fbF18381A38) {
         oracleAdr = 0xECe365B379E1dD183B20fc5f022230C044d51404;
      } 
      if (targetAsset == 0xc8C3A0d984c7073d1557c5E6F861BA7f81678738) {
         oracleAdr = 0x8A753747A1Fa494EC906cE90E9f37563A8AF630e;
      }

      AggregatorV2V3Interface priceFeedCurrency = AggregatorV2V3Interface(oracleAdr);
      uint256 ucurrval = (usd*((10**18)))/(uint(priceFeedCurrency.latestAnswer()));
      return ucurrval;
  }

   

}

